import React, { useState, useContext } from 'react';
import { ProductContext } from '../../contexts/ProductContext';
import Input from '../UI/Input';
import Textarea from '../UI/Textarea';
import Button from '../UI/Button';

const ProductForm = ({ product, onClose }) => {
  const { dispatch } = useContext(ProductContext);
  const [formData, setFormData] = useState(product || { name: '', description: '', price: '' });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (product) {
      dispatch({ type: 'UPDATE_PRODUCT', payload: { ...formData, id: product.id } });
    } else {
      dispatch({ type: 'ADD_PRODUCT', payload: { ...formData, id: Date.now() } });
    }
    onClose();
  };

  return (
    <form onSubmit={handleSubmit}>
      <Input
        label="Nom du produit"
        name="name"
        value={formData.name}
        onChange={handleChange}
      />
      <Textarea
        label="Description"
        name="description"
        value={formData.description}
        onChange={handleChange}
      />
      <Input
        label="Prix"
        type="number"
        name="price"
        value={formData.price}
        onChange={handleChange}
      />
      <Button type="submit" className="btn-primary">
        {product ? 'Mettre à jour' : 'Ajouter'}
      </Button>
    </form>
  );
};

export default ProductForm;
