import React, { useState, useContext } from 'react';
import { AnnouncementContext } from '../../contexts/AnnouncementContext';
import Input from '../UI/Input';
import Textarea from '../UI/Textarea';
import Button from '../UI/Button';

const AnnouncementForm = ({ announcement, onClose }) => {
  const { addAnnouncement, updateAnnouncement } = useContext(AnnouncementContext);
  const [formData, setFormData] = useState(announcement || { title: '', content: '', date: '' });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (announcement) {
      updateAnnouncement({ ...formData, id: announcement.id });
    } else {
      addAnnouncement({ ...formData, id: Date.now() });
    }
    onClose();
  };

  return (
    <form onSubmit={handleSubmit}>
      <Input
        label="Titre de l'annonce"
        name="title"
        value={formData.title}
        onChange={handleChange}
      />
      <Textarea
        label="Contenu"
        name="content"
        value={formData.content}
        onChange={handleChange}
      />
      <Input
        label="Date"
        type="date"
        name="date"
        value={formData.date}
        onChange={handleChange}
      />
      <Button type="submit" className="btn-primary">
        {announcement ? 'Mettre à jour' : 'Ajouter'}
      </Button>
    </form>
  );
};

export default AnnouncementForm;
